package day1;

public class if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int c=10, d=20,e=30;
		
		if (c<d && d<e)
		{
		
			System.out.println("c is smallest");
		}
	
		else if (d<e && c<d)
			System.out.println("d is smallest");
		
		else
			System.out.println("e is smallest");
		}
		
		
	}


