package day1;

public class whilelooping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		
		int i = 1;   //initillization  -step1
		
		while (i <= 10)   //conditioan   -step2
			
		{
			System.out.println(i);     //step3
			i++;                       //step4
	
		
		
			
		}
	}

}
