package selfpractice;

public class Sample0perators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 100;
		int b = 200;
		
		System.out.println("Value of" + (a+b));
		System.out.println("Value of" + (a-b));
		System.out.println("Value of" + (a%b));
		System.out.println("Value of" + (a*b));
		System.out.println("Value of" + (a/b));
	
		
		
	}

}
